import React, { useState, useEffect } from 'react';
import { NavigationContainer } from '@react-navigation/native';
import { createStackNavigator } from '@react-navigation/stack';
import { Provider as PaperProvider } from 'react-native-paper';
import { StatusBar } from 'expo-status-bar';
import AsyncStorage from '@react-native-async-storage/async-storage';

import WelcomeScreen from './src/screens/WelcomeScreen';
import AuthScreen from './src/screens/AuthScreen';
import DashboardScreen from './src/screens/DashboardScreen';
import ExpensesScreen from './src/screens/ExpensesScreen';
import SavingsScreen from './src/screens/SavingsScreen';
import CurrentsScreen from './src/screens/CurrentsScreen';
import AddTransactionScreen from './src/screens/AddTransactionScreen';
import FamilyMembersScreen from './src/screens/FamilyMembersScreen';
import CustomSectionsScreen from './src/screens/CustomSectionsScreen';
import { theme } from './src/theme';

const Stack = createStackNavigator();

export default function App() {
  const [isFirstLaunch, setIsFirstLaunch] = useState(null);
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    checkFirstLaunch();
    checkAuth();
  }, []);

  const checkFirstLaunch = async () => {
    try {
      const hasLaunched = await AsyncStorage.getItem('hasLaunched');
      setIsFirstLaunch(hasLaunched === null);
      setLoading(false);
    } catch (error) {
      console.error('Error checking first launch:', error);
      setIsFirstLaunch(true);
      setLoading(false);
    }
  };

  const checkAuth = async () => {
    try {
      const familyData = await AsyncStorage.getItem('familyData');
      setIsAuthenticated(familyData !== null);
    } catch (error) {
      console.error('Error checking auth:', error);
    }
  };

  if (loading) {
    return null;
  }

  return (
    <PaperProvider theme={theme}>
      <StatusBar style="auto" />
      <NavigationContainer>
        <Stack.Navigator
          screenOptions={{
            headerStyle: {
              backgroundColor: theme.colors.primary,
            },
            headerTintColor: '#fff',
            headerTitleStyle: {
              fontWeight: 'bold',
            },
          }}
        >
          {isFirstLaunch ? (
            <Stack.Screen
              name="Welcome"
              component={WelcomeScreen}
              options={{ headerShown: false }}
            />
          ) : !isAuthenticated ? (
            <Stack.Screen
              name="Auth"
              component={AuthScreen}
              options={{ headerShown: false }}
            />
          ) : (
            <>
              <Stack.Screen
                name="Dashboard"
                component={DashboardScreen}
                options={{ title: 'Family Dashboard' }}
              />
              <Stack.Screen
                name="Expenses"
                component={ExpensesScreen}
                options={{ title: 'Expenses' }}
              />
              <Stack.Screen
                name="Savings"
                component={SavingsScreen}
                options={{ title: 'Savings' }}
              />
              <Stack.Screen
                name="Currents"
                component={CurrentsScreen}
                options={{ title: 'Current Account' }}
              />
              <Stack.Screen
                name="AddTransaction"
                component={AddTransactionScreen}
                options={{ title: 'Add Transaction' }}
              />
              <Stack.Screen
                name="FamilyMembers"
                component={FamilyMembersScreen}
                options={{ title: 'Family Members' }}
              />
              <Stack.Screen
                name="CustomSections"
                component={CustomSectionsScreen}
                options={{ title: 'Custom Sections' }}
              />
            </>
          )}
        </Stack.Navigator>
      </NavigationContainer>
    </PaperProvider>
  );
}
